    <header className="block row center">

      <div>                     {/*  17 */}
        <a href="#/cart">
        <img src={CartIcon} alt="" width="30"/>{' '}
          {props.countCartItems ? (
            <button className="badge">{props.countCartItems}</button>
          ) : (
            ''
          )}
        </a>{' '}

      </div>
    </header>
  );
}
